import React from "react";

export default class FetchWeather extends React.Component {
  state = {
    loading: true,
    weather: null,
    city: null,
  };

  async componentDidMount() {
    const request = await fetch(
      "http://api.openweathermap.org/data/2.5/forecast?id=3623484&mode=json&units=metric&appid=fdedcbb70a2fb6a9f6ff81929d1316d6"
    ).then((res) => res.json());

    console.log({ request });

    const weatherFromRequest = request.list.map((wSet) => {
      const cleanWSet = {
        date: wSet.dt_txt,
        weather: wSet.weather[0],
        temperature: wSet.main,
      };
      return cleanWSet;
    });

    const update = {
      city: request.city,
      loading: false,
      weather: weatherFromRequest,
    };

    this.setState(update);
  }

  render() {
    return (
      <div>
        {this.state.city && (
          <div>
            <h2>{this.state.city.name}</h2>
            <h3>{this.state.city.country}</h3>
          </div>
        )}

        {this.state.loading ? (
          <div> loading ... </div>
        ) : (
          <div>
            <h3>Weather list</h3>

            <div>
              {this.state.weather.map((set) => {
                return <li key={set.date}>{set.date}</li>;
              })}
            </div>
          </div>
        )}
      </div>
    );
  }
}
